// src/pages/home.js
import React from "react";
import homepage from "../data/sitedata.js";
import Link from '../components/Link';
import { Transition } from 'react-transition-group';

const duration = 300;

const defaultStyle = {
  transition: `opacity ${duration}ms ease-in-out`,
  opacity: 0,
}

const transitionStyles = {
  entering: { left: -600 },
  entered:  { left: -600 },
  exiting:  { left: 0 },
  exited:  { left: 0 },
};

const Home = ({ in: inProp }) => {

  return (
    <div>
      <div className="hero">
        <div className="five columns">
          <h1>Andrew Fitzpatrick</h1><h3>.rocks</h3>
        </div>
        <div className="seven columns">
          <Transition in={inProp} timeout={duration}>
            <img
              src={require('../images/fitz_pic.jpg')}
              className="heroImage"
              alt="Andrew Fitzpatrick rocks"
              style={{
                ...defaultStyle,
                ...transitionStyles[state]
              }}
            />
           </Transition>
        </div>
      </div>
      <div className="fitzfolio full-width"><h2>FITZ-FOLIO</h2></div>
      <div>
      {
          homepage.map((item, i) => (
            <div className = "home-port" key={i}>
              <Link to = {item.link} key={i}>
                <h4>{item.company}</h4>
              </Link>
                <div><h4>{item.title}</h4></div>
                <div><h4>{item.date}</h4></div>
            </div>
          ))
        }
      </div>
    </div>
  );
};

export default Home;
