import React, { Component } from 'react';
import './App.scss';
import Page from './components/Page';
import Nav from './components/Nav';
import Link from './components/Link';

import Header from "./components/Header.js";
// import Footer from "./components/Footer.js";

import Home from "./pages/home";
import About from "./pages/about";
import Contact from "./pages/contact";
import Restaurants from "./pages/restaurants";
import Cmcdems from "./pages/cmc-dems";
import Pinball from "./pages/pacific-pinball";
import Fitztastic from "./pages/fitztastic";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {left: 0}
  }


  render() {
    return (
      <div className="App">
        <Header />
        <div>
          <Link to="/about" >
            <button>About</button>
          </Link>
          <Link to="/red" >
            <button>red</button>
          </Link>
          <Link to="/blue" >
            <button>blue</button>
          </Link>
          <Link to="/brown" >
            <button>brown</button>
          </Link>

        </div>
        <Nav>
          <Page path="/" component={Home} />
          <Page path="/about"  component={About} />
          <Page path="/contact" component={Contact} />
          <Page path="/sciarra-restaurants"  component={Restaurants} />
          <Page path="/cmc-dems"  component={Cmcdems} />
          <Page path="/pacific-pinball" component={Pinball} />
          <Page path="/fitztastic" component={Fitztastic} />
        </Nav>

      </div>
    );
  }
}
export default App

