// src/pages/home.js
import React from "react";
import homepage from "../data/sitedata.js";
import Link from '../components/Link';
import { Transition } from 'react-transition-group';
import { Waypoint } from 'react-waypoint';

const duration = 10000;

const defaultStyle = {
  transition: `opacity ${duration}ms ease-in-out`,
  opacity: 0,
}

const transitionStyles = {
  entering: { opacity: 0 },
  entered:  { opacity: 1 },
  exiting:  { opacity: 0 },
  exited:  { opacity: 1 },
};

_handleEnter = ({ in: inProp }) => {
  <Transition in={inProp} timeout={duration}>
    {state => (
      <div
        className = "home-port"
        key={i}
        style={{
          ...defaultStyle,
          ...transitionStyles[state],
        }}
      >
        <Link to = {item.link} key={i}>
          <h4>{item.company}</h4>
        </Link>
          <div><h4>{item.title}</h4></div>
          <div><h4>{item.date}</h4></div>
      </div>
    )}
  </Transition>
}

const Home = () => {
  return (
    <div>
      <div className="hero">
        <div className="five columns">
          <h1>Andrew Fitzpatrick</h1><h3>.rocks</h3>
        </div>
        <div className="seven columns wrapper">
          <img src={require('../images/fitz_pic.jpg')} id="slide" className="heroImage" alt="Andrew Fitzpatrick rocks"/>
        </div>
      </div>

      <div
        className="fitzfolio full-width"
      >
        <h2>FITZ-FOLIO</h2>
      </div>


      <div>
      {
          homepage.map((item, i) => (
            <Waypoint onEnter={this._handleEnter} />
          ))
        }
      </div>
    </div>
  );
};

export default Home;
