// src/pages/home.js
import React from "react";
import homepage from "../data/sitedata.js";
import Link from '../components/Link';
import ReactCSSTransitionGroup from 'react-transition-group';
import { Waypoint } from 'react-waypoint';

// const duration = 10000;

// const defaultStyle = {
//   transition: `opacity ${duration}ms ease-in-out`,
//   opacity: 0,
// }

// const transitionStyles = {
//   entering: { opacity: 0 },
//   entered:  { opacity: 1 },
//   exiting:  { opacity: 0 },
//   exited:  { opacity: 1 },
// };

const handleEnter = ({ in: inProp }) => {

}

const Home = () => {
  return (
    <div>
      <div className="hero">
        <div className="five columns">
          <h1>Andrew Fitzpatrick</h1><h3>.rocks</h3>
        </div>
        <div className="seven columns wrapper">
          <img src={require('../images/fitz_pic.jpg')} id="slide" className="heroImage" alt="Andrew Fitzpatrick rocks"/>
        </div>
      </div>

      <div className="fitzfolio full-width">
        <h2>FITZ-FOLIO</h2>
      </div>

      <div>
        { homepage.map((item, i) => (
          <Waypoint onEnter={this._handleEnter}>
            <ReactCSSTransitionGroup
              transitionName="example"
              transitionAppear={true}
              transitionAppearTimeout={500}
              transitionEnter={false}
              transitionLeave={false}
            >
              <div className = "home-port" key={i}>
                <Link to = {item.link} key={i}>
                  <h4>{item.company}</h4>
                </Link>
                  <div><h4>{item.title}</h4></div>
                  <div><h4>{item.date}</h4></div>
              </div>
            </ReactCSSTransitionGroup>
          </Waypoint>
        ))}
      </div>
    </div>
  );
};

export default Home;
