// src/pages/home.js
import React from 'react';
import homepage from "../data/sitedata.js";
import Link from '../components/Link';
import Image from '../components/Image';
import FadeInSection from '../components/Fadein';

class Home extends React.Component {

constructor(props) {
  super(props)
  this.state = {
    showHoverImage: false
  }
}



  render() {
    const jobTitle = [item.company, item.title, item.date];
    return(
      <div>
        <div className="hero">
          <div className="five columns over">
            <h1>Andrew Fitzpatrick</h1><h3>.rocks</h3>
          </div>
          <div className="seven columns wrapper under">
            <img
              src={require('../images/fitz_pic.jpg').default}
              id="slide" className="heroImage"
              alt="Andrew Fitzpatrick rocks"
            />
          </div>
        </div>
        <div className="fitzfolio full-width">
          <h2>FITZ-FOLIO</h2>
        </div>

        { homepage.map((item, i) => (
        <div
          className = "full-width"
          key={i}
          style={{backgroundColor: item.bgcolor}}
        >
          <div className="home-port">
          <FadeInSection>

            <Link to = {item.link}>
              { jobTitle.map((job, i) => (
                <h4 key={i}>
                  {job}
                </h4>
              ))}
            </Link>


            <div className="row">
            { item.images.map((logo, i) => (
              <div className= "one-third column" key={i}>
                <Link to = {logo.link}>
                  <Image logo = {logo}/>
                </Link>
              </div>
            ))}
            </div>
            </FadeInSection>
          </div>
        </div>
        ))}
      </div>
    );
  };
};

export default Home;
